'use strict';

module.exports = once;

function once(func) {
  if (typeof func != 'function') {
    throw new Error('Parameter must be function');
  }
  var result;
  return function() {
    if (func) {
      var tempFunc = func;
      func = undefined;
      result = tempFunc.apply(this, arguments);
    }
    return result;
  };
}
